create table "public"."shops" (
    "id" integer generated by default as identity not null,
    "uuid" uuid not null,
    "name" character varying(255) not null,
    "address" character varying(255) not null,
    "genre" character varying(20) not null,
    "url" character varying(255) not null,
    "lunch" character varying(20) not null,
    "card" character varying(20) not null,
    "created_at" timestamp with time zone,
    "updated_at" timestamp with time zone
);


CREATE UNIQUE INDEX shops_pkey ON public.shops USING btree (id);


alter table "public"."shops" add constraint "shops_pkey" PRIMARY KEY using index "shops_pkey";
alter table "public"."shops" add constraint "shops_uuid_fkey" FOREIGN KEY (uuid) REFERENCES users(uuid) not valid;
alter table "public"."shops" validate constraint "shops_uuid_fkey";
